import logo from "./logo.svg";
import "./App.css";

function App() {
  let lvl = 3;
  let money = 12500;
  let hours = 1;
  let minutes = 22;
  let seconds = 36;
  let cpu = 'Z6'

  let currentState = "MainMenu";
  switch (currentState) {
    case "MainMenu":
      return (
        <div className="App">
          {/* Main Menu */}
          <div class="main-menu">
            <h1>LearnTheBasics.it</h1>
            <div class="button-container">
              <button>Continue</button>
              <button>New Game</button>
              <button>Options</button>
              <button>Quit Game</button>
            </div>
            <div id="darken-bg"></div>
          </div>

          {/* Save Menu */}

          <div class="save-container">
            <div class="save-item" id="save-item1">
              <div class="grid-item save-top">
                <div id="langs">
                  <p id="html">HTML</p>
                  <p id="css">CSS</p>
                  <p id="js">JS</p>
                </div>
                <div id="config"> 
                  <p>CPU: {cpu}</p>
                  <p></p>
                  <p></p>
                  <p></p>
                </div>
              </div>
              <div class="grid-item save-bottom">
                <p>LvL: {lvl}</p>
                <p>{money}$</p>
                <p id="playtime">
                  {hours}:{minutes}:{seconds}
                </p>
              </div>
            </div>
            <div class="save-item" id="save-item2"></div>
            <div class="save-item" id="save-item3"></div>
          </div>
        </div>
      );
      break;
  }
}

export default App;
